mapper.select:
SELECT t2.id empId,t2.emp_name empName,t2.lat lat,t2.lng lng
FROM  business_data t2
where t2.tags2 LIKE CONCAT(
'%',
(
	SELECT t3.`name`
	FROM industroy_tupu t3
	WHERE t3.id = #{induId}
),
'%'
)
AND CONCAT("中国,",t2.sheng) in(
SELECT mername
from region t1
where id = #{regionId}
)

SELECT t2.id empId,t2.emp_name empName,t2.lat lat,t2.lng lng
FROM  business_data t2
where t2.tags2 LIKE CONCAT(
'%',
(
	SELECT t3.`name`
	FROM industroy_tupu t3
	WHERE t3.id = #{induId}
),
'%'
)
AND CONCAT("中国,",t2.sheng,",",t2.city) in(
SELECT mername
from region t1
where id = #{regionId}
)

SELECT t2.id empId,t2.emp_name empName,t2.lat lat,t2.lng lng
FROM  business_data t2
where t2.tags2 LIKE CONCAT(
'%',
(
	SELECT t3.`name`
	FROM industroy_tupu t3
	WHERE t3.id =  #{induId}
),
'%'
)
AND CONCAT("中国,",t2.sheng,",",t2.city,",",t2.add3) in(
SELECT mername
from region t1
where id = #{regionId}
)
